import axios from 'axios';
import { createContext, useContext, useState } from 'react';
import { Navigate, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import AgentsPage from './pages/AgentsPage';
import Dashboard from './pages/Dashboard';
import LoginPage from './pages/LoginPage';
import TodoApp from './pages/TodoApp';
import UploadPage from './pages/UploadPage';

// Simple styles import
import './styles.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000/api';
console.log('[App] using API_BASE =', API_BASE);

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export default function App() {
  const [user, setUser] = useState(() => {
    const userData = localStorage.getItem('userData');
    return userData ? JSON.parse(userData) : null;
  });
  const [loading, setLoading] = useState(false);

  const login = async (email, password) => {
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/auth/login`, { email, password });
      const { token, _id, name, email: userEmail, role } = res.data;
      if (!token || !role) {
        throw new Error('Invalid server response: missing token or role');
      }
      const userData = { token, _id, name, email: userEmail, role };
      localStorage.setItem('userData', JSON.stringify(userData));
      setUser(userData);
      setLoading(false);
      return { success: true };
    } catch (err) {
      setLoading(false);
      return { success: false, message: err.response?.data?.message || err.message };
    }
  };

  const logout = () => {
    localStorage.removeItem('userData');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, loading }}>
      <Router>
        <Routes>
          <Route path="/" element={user ? <Navigate to="/dashboard" /> : <Navigate to="/login" />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/dashboard" element={user ? <Dashboard /> : <Navigate to="/login" />} />
          <Route path="/agents" element={user ? <AgentsPage /> : <Navigate to="/login" />} />
          <Route path="/upload" element={user ? <UploadPage /> : <Navigate to="/login" />} />
          <Route path="/todo" element={<TodoApp />} />
        </Routes>
      </Router>
    </AuthContext.Provider>
  );
}
